import React, { useState } from 'react'
import Header from './Header'
import Sidebar from './Sidebar'
import SubjectManager from './SubjectManager'
import ReportGenerator from './ReportGenerator'

export interface Question {
  id: string
  type: string
  category: string
  difficulty: string
  score: number
  maxScore: number
  source: string
  unit: string
  content: string
  solution: string
  feedback: string
  timeSpent: number
  tags: string[]
}

export interface Subject {
  id: string
  name: string
  questions: Question[]
}

export interface ExamInfo {
  studentName: string
  studentId: string
  className: string
  examType: string
  examName: string
  examDate: string
  examTime: number
  schoolName: string
  teacherName: string
  subject: string
  totalScore: number
  passingScore: number
  grade: string
  rank: string
  notes: string
}

const GradeAnalysisSystem: React.FC = () => {
  const [subjects, setSubjects] = useState<Subject[]>([])
  const [examInfo, setExamInfo] = useState<ExamInfo>({
    studentName: '',
    studentId: '',
    className: '',
    examType: 'ì¤‘ê°„ê³ ì‚¬',
    examName: '',
    examDate: '',
    examTime: 50,
    schoolName: '',
    teacherName: '',
    subject: '',
    totalScore: 0,
    passingScore: 60,
    grade: '',
    rank: '',
    notes: ''
  })
  const [activeTab, setActiveTab] = useState<'subjects' | 'report'>('subjects')

  const addSubject = (subjectName: string = '') => {
    const newSubject: Subject = {
      id: Date.now().toString(),
      name: subjectName || `ê³¼ëª© ${subjects.length + 1}`,
      questions: []
    }
    setSubjects([...subjects, newSubject])
  }

  const removeSubject = (id: string) => {
    setSubjects(subjects.filter(subject => subject.id !== id))
  }

  const updateSubject = (id: string, updatedSubject: Partial<Subject>) => {
    setSubjects(subjects.map(subject => 
      subject.id === id ? { ...subject, ...updatedSubject } : subject
    ))
  }

  const addQuestion = (subjectId: string) => {
    const newQuestion: Question = {
      id: Date.now().toString(),
      type: 'ê°ê´€ì‹',
      category: 'ì§€ì‹',
      difficulty: 'ì¤‘',
      score: 0,
      maxScore: 5,
      source: 'êµê³¼ì„œ',
      unit: '1ë‹¨ì›',
      content: '',
      solution: '',
      feedback: '',
      timeSpent: 0,
      tags: []
    }

    setSubjects(subjects.map(subject => 
      subject.id === subjectId 
        ? { ...subject, questions: [...subject.questions, newQuestion] }
        : subject
    ))
  }

  const removeQuestion = (subjectId: string, questionId: string) => {
    setSubjects(subjects.map(subject => 
      subject.id === subjectId 
        ? { ...subject, questions: subject.questions.filter(q => q.id !== questionId) }
        : subject
    ))
  }

  const updateQuestion = (subjectId: string, questionId: string, updatedQuestion: Partial<Question>) => {
    setSubjects(subjects.map(subject => 
      subject.id === subjectId 
        ? { 
            ...subject, 
            questions: subject.questions.map(q => 
              q.id === questionId ? { ...q, ...updatedQuestion } : q
            )
          }
        : subject
    ))
  }

  return (
    <div className="max-w-7xl mx-auto p-6">
      <Header />

      <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
        <div className="lg:col-span-1">
          <Sidebar 
            examInfo={examInfo}
            setExamInfo={setExamInfo}
            activeTab={activeTab}
            setActiveTab={setActiveTab}
          />
        </div>

        <div className="lg:col-span-3">
          {activeTab === 'subjects' ? (
            <SubjectManager
              subjects={subjects}
              addSubject={addSubject}
              removeSubject={removeSubject}
              updateSubject={updateSubject}
              addQuestion={addQuestion}
              removeQuestion={removeQuestion}
              updateQuestion={updateQuestion}
            />
          ) : (
            <ReportGenerator 
              subjects={subjects}
              examInfo={examInfo}
            />
          )}
        </div>
      </div>
    </div>
  )
}

export default GradeAnalysisSystem

