import time
import uuid
from typing import Dict, List, Optional, Any
import jwt
import httpx
from pydantic import BaseModel


class StealthMoleConfig(BaseModel):
    """Configuration for StealthMole API."""
    access_key: str
    secret_key: str
    base_url: str = "https://sm.stealtmole.com"


class StealthMoleClient:
    """Client for interacting with StealthMole API."""

    def __init__(self, config: StealthMoleConfig):
        self.config = config
        self.client = httpx.AsyncClient(timeout=30.0)

    def _generate_jwt_token(self) -> str:
        """Generate JWT token for authentication."""
        payload = {
            "access_key": self.config.access_key,
            "nonce": str(uuid.uuid4()),
            "iat": int(time.time())
        }
        return jwt.encode(payload, self.config.secret_key, algorithm="HS256")

    def _get_headers(self) -> Dict[str, str]:
        """Get headers with JWT authentication."""
        token = self._generate_jwt_token()
        return {
            "Authorization": f"Bearer {token}",
            "Content-Type": "application/json"
        }

    async def _make_request(self, endpoint: str, params: Optional[Dict] = None) -> Dict[str, Any]:
        """Make authenticated request to StealthMole API."""
        url = f"{self.config.base_url}{endpoint}"
        headers = self._get_headers()

        response = await self.client.get(url, headers=headers, params=params or {})
        response.raise_for_status()
        return response.json()

    async def search_darkweb(self, indicator: str, target: str = "all", limit: int = 50) -> Dict[str, Any]:
        """Search dark web content."""
        endpoint = f"/v2/dt/{indicator}/target/{target}"
        params = {"limit": limit}
        return await self._make_request(endpoint, params)

    async def search_telegram(self, indicator: str, target: str = "all", limit: int = 50) -> Dict[str, Any]:
        """Search Telegram content."""
        endpoint = f"/v2/tt/{indicator}/target/{target}"
        params = {"limit": limit}
        return await self._make_request(endpoint, params)

    async def search_credentials(self, indicator: str, limit: int = 50) -> Dict[str, Any]:
        """Search for leaked credentials."""
        endpoint = f"/v2/cl/{indicator}"
        params = {"limit": limit}
        return await self._make_request(endpoint, params)

    async def search_ransomware(self, indicator: str, limit: int = 50) -> Dict[str, Any]:
        """Search ransomware monitoring data."""
        endpoint = f"/v2/rm/{indicator}"
        params = {"limit": limit}
        return await self._make_request(endpoint, params)

    async def get_node_details(self, service: str, node_id: str) -> Dict[str, Any]:
        """Get detailed information about a specific node."""
        endpoint = f"/v2/{service}/node/{node_id}"
        return await self._make_request(endpoint)

    async def export_data(self, service: str, indicator: str, format: str = "json") -> Dict[str, Any]:
        """Export data in specified format."""
        endpoint = f"/v2/{service}/{indicator}/export"
        params = {"format": format}
        return await self._make_request(endpoint, params)

    async def get_targets(self, service: str, indicator: str) -> Dict[str, Any]:
        """Get available targets for a service and indicator."""
        endpoint = f"/v2/{service}/{indicator}/targets"
        return await self._make_request(endpoint)

    async def close(self):
        """Close the HTTP client."""
        await self.client.aclose()

