{
  "name": "stealthmole-mcp-server",
  "version": "1.0.0",
  "description": "StealthMole MCP Server - A TypeScript implementation for dark web and cybersecurity threat intelligence",
  "main": "dist/index.js",
  "type": "module",
  "bin": {
    "stealthmole-mcp-server": "dist/index.js"
  },
  "scripts": {
    "prebuild": "shx rm -rf dist",
    "build": "tsc",
    "prepare": "npm run build",
    "watch": "tsc --watch",
    "start": "node dist/index.js"
  },
  "keywords": [
    "mcp",
    "stealthmole",
    "cybersecurity",
    "dark-web",
    "threat-intelligence",
    "typescript"
  ],
  "author": "realsun",
  "license": "MIT",
  "engines": {
    "node": ">=18"
  },
  "dependencies": {
    "@modelcontextprotocol/sdk": "^1.10.2",
    "axios": "^1.6.7",
    "dotenv": "^16.4.5",
    "jsonwebtoken": "^9.0.2",
    "uuid": "^11.1.0",
    "zod": "^3.22.4",
    "zod-to-json-schema": "^3.23.5"
  },
  "devDependencies": {
    "@types/jsonwebtoken": "^9.0.10",
    "@types/node": "^22",
    "@types/uuid": "^10.0.0",
    "shx": "^0.3.4",
    "typescript": "^5.3.3"
  },
  "repository": {
    "type": "git",
    "url": "git+https://github.com/realsun/stealthmole-mcp-server.git"
  },
  "bugs": {
    "url": "https://github.com/realsun/stealthmole-mcp-server/issues"
  },
  "homepage": "https://github.com/realsun/stealthmole-mcp-server#readme"
}
